        this.file = {
            getJSON: fileGetJSON.bind(this),
            getEntryData: getEntryData.bind(this),
            getEntryLink: fileGetEntryLink.bind(this),
            getJSONEncrypted: getJSONEncrypted.bind(this),
        };
