        this.db = {
            deleteJSON: deleteJSON.bind(this),
            getJSON: getJSON.bind(this),
            setJSON: setJSON.bind(this),
            setDataLink: setDataLink.bind(this),
            getRawBytes: getRawBytes.bind(this),
        };
